update app_config set config_value='{ "messageDescription": "Description for message", "time": "19:43", "timeDescription": "Description for time", "configuration": { "daysBefore": [ 5, 4, 3, 2 ], "afterNoOfHours": [ 0 ] }, "configurationDescription": "Description for configuration", "sql": "", "subjectDescription": "Description for reminder notification subject", "messageReport": "<p>The report <strong>%REPORT_NAME%</strong>&nbsp;for <strong>%GRANT_NAME%</strong> from <strong>%TENANT%&nbsp;</strong>is due on %DUE_DATE%.</p> <p>Please log on to Anudan to submit the report.</p> <p>In case you have any questions or need clarifications while submitting the report please reach out to <strong>%OWNER_NAME%</strong> at <strong>%OWNER_EMAIL%</strong>.</p> <p><i>Please ignore this reminder if you have already submitted the report.</i></p>", "subjectReport": "Alert | Report Submission Reminder | Action Required" }' where config_name='DUE_REPORTS_REMINDER_SETTINGS';
