update data_export_config set query='select b.name as organization_name,a.reference_no, a.name as grant_name,d.name as grant_type, a.start_date,a.end_date,a.amount, case when d.internal then b.name else (select name from organizations where id=a.organization_id) end as implementing_organization, a.representative as implementing_org_rep, case when orig_grant_id is not null then ''Yes'' else ''No'' end as is_amended, string_agg(ot.name,'', '') as grant_tags from grants a inner join organizations b on b.id=a.grantor_org_id inner join workflow_statuses c on c.id=a.grant_status_id inner join grant_types d on d.id=a.grant_type_id left join grant_tags gt on gt.grant_id=a.id left join org_tags ot on ot.id=gt.org_tag_id where b.id=%tenantId% and c.internal_status=''ACTIVE'' and a.deleted=false group by b.name,a.reference_no,a.name,d.name,a.start_date,a.end_date,a.amount,d.internal,a.organization_id,a.representative,orig_grant_id' where name='Active Grants' and category='ACTIVE_GRANTS_DETAILS';